package pjav;

public class ArrayDeclarationType1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int a [] = new int[5];
		
		a[0] = 90;
		a[1] = 28;
		a[2] = 82;
		a[3] = 7;
		a[4] = 47;
		
		for (int i = 0; i < a.length; i++) {
			System.out.println(a[i]);
		}
		
	}

}
